import React, { useState, useEffect } from "react";
import axios from "axios";

export default function App() {
  const [prompt, setPrompt] = useState("");
  const [response, setResponse] = useState([]);

  useEffect(() => {
    console.log(response);
  }, [response]);

  const maxTokens = 20;

  const handleChange = (e) => {
    setPrompt(e.target.value);
  };

  const getResponse = () => {
    axios
      .post(
        "https://api.openai.com/v1/engines/text-curie-001/completions",
        { prompt },
        {
          headers: {
            "Content-Type": "application/json",
            Authorization: "Bearer " + process.env.REACT_APP_OPENAI_APIKEY,
            max_tokens: maxTokens,
          },
        }
      )
      .then((res) => {
        setResponse([
          ...response,
          { prompt: prompt, response: res.data.choices[0].text.trim() },
        ]);
      });
  };

  const submit = () => {
    getResponse();
  };

  return (
    <div className="App">
      <h1>Fun with AI</h1>
      <div>
        <form>
          <label>Enter prompt:</label>
          <br />
          <input
            placeholder="enter an prompt"
            type="text"
            name="prompt"
            value={prompt}
            onChange={handleChange}
          ></input>
        </form>
        <button onClick={submit}>Submit</button>
      </div>
      <br />
      <br />
      <div>
        {response.map(function (d, idx) {
          return (
            <div className="response" key={idx}>
              <li>Prompt: {d.prompt}</li>
              <li>Response: {d.response}</li>
              <br />
            </div>
          );
        })}
      </div>
    </div>
  );
}
